import { useState } from 'react';
import { Search, FileText, Tag } from 'lucide-react';

export default function VaultViewer() {
  const [conversations] = useState([
    {
      id: 1,
      title: "Export File Understanding",
      date: "2024-12-22",
      tags: ["exports", "tutorial"],
      excerpt: "Learning about Claude conversation exports..."
    },
    {
      id: 2,
      title: "Code Examples Collection",
      date: "2024-12-22",
      tags: ["code", "examples"],
      excerpt: "Various programming examples for reference..."
    }
  ]);

  const [searchTerm, setSearchTerm] = useState('');

  const filteredConvs = conversations.filter(conv =>
    conv.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    conv.tags.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 to-slate-800 p-8">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-4xl font-bold text-white mb-8 flex items-center gap-3">
          <FileText size={40} />
          Claude Vault
        </h1>

        <div className="relative mb-6">
          <Search className="absolute left-4 top-3.5 text-slate-400" size={20} />
          <input
            type="text"
            placeholder="Search conversations..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-12 pr-4 py-3 bg-slate-800 border border-slate-700 rounded-lg text-white placeholder-slate-400 focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>

        <div className="space-y-4">
          {filteredConvs.map(conv => (
            <div
              key={conv.id}
              className="bg-slate-800 border border-slate-700 rounded-lg p-6 hover:border-blue-500 transition-all cursor-pointer"
            >
              <h2 className="text-xl font-semibold text-white mb-2">
                {conv.title}
              </h2>
              <p className="text-slate-400 text-sm mb-3">{conv.excerpt}</p>
              <div className="flex items-center justify-between">
                <div className="flex gap-2">
                  {conv.tags.map(tag => (
                    <span
                      key={tag}
                      className="flex items-center gap-1 px-3 py-1 bg-blue-500/20 text-blue-400 rounded-full text-xs"
                    >
                      <Tag size={12} />
                      {tag}
                    </span>
                  ))}
                </div>
                <span className="text-slate-500 text-sm">{conv.date}</span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
